    background: linear-gradient(135deg, hsl(207, 90%, 54%) 0%, hsl(207, 90%, 44%) 100%);
  }

  .gradient-secondary {
    background: linear-gradient(135deg, hsl(16, 100%, 60%) 0%, hsl(16, 100%, 50%) 100%);
