/* Newsletter section styling */
.newsletter-bg {
  background: linear-gradient(135deg, hsl(207, 90%, 54%) 0%, hsl(207, 90%, 44%) 50%, hsl(16, 100%, 60%) 100%);
}

/* FAQ accordion styling */
.faq-content {
  max-height: 0;
